import React, { useEffect, useState } from 'react';
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message }) {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (show) {
      setIsVisible(true); // Make the toast visible

      // Set the toast to fade out after 30 seconds
      const fadeOutTimer = setTimeout(() => {
        setIsVisible(false); // Start the fade-out process
      }, 30000); // Adjust the duration as needed

      // Cleanup function to clear timeout if the component unmounts
      return () => clearTimeout(fadeOutTimer);
    }
  }, [show]);

  if (!show || !isVisible) {
    return null;
  }

  return (
    <div className={`toast ${!isVisible ? 'fade-out' : ''}`}>
      <Check className="toast-icon" size={24} color="#4CAF50" />
      <span className="toast-message">{message}</span>
      <button className="toast-view-button">View</button>
    </div>
  );
}

export default Toast;
