import React, { useEffect, useState } from 'react'
import './App.css';
import AddProjectForm from './components/AddProjectForm';

function App() {

  const [data, setData] = useState([])

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = () => {
    fetch("http://localhost:8080/projects")
      .then(res => res.json())
      .then(data => setData(data.map(project => ({ ...project, isEditing: false }))))
      .catch(err => console.error(err));
  };

  const addProject = (project) => {
    fetch("http://localhost:8080/projects", {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        title: project.title,
        contents: project.contents,
        stack: project.stack,
        team_name: project.team_name,
        team_members: project.team_members
      }),
    })
    .then(response => response.json())
    .then(() => fetchProjects())
    .catch(err => console.error(err));
  };

  const deleteProject = (id) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this project?");
    if (!isConfirmed) {
      return;
    }

    fetch(`http://localhost:8080/projects/${id}`, {
      method: 'DELETE',
    })
    .then(response => {
      if (response.ok) {
        setData(data.filter(project => project.id !== id));
      } else {
        alert('Failed to delete the project');
      }
    })
    .catch(err => console.error('Error:', err));
  };  

  const updateProject = (id, updatedProject) => {
    fetch(`http://localhost:8080/projects/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedProject),
    })
    .then(response => response.json())
    .then(() => fetchProjects())
    .catch(err => console.error(err));
  };

  const toggleEdit = (id) => {
    setData(data.map(project => project.id === id ? { ...project, isEditing: !project.isEditing } : project));
  };

  const handleEditChange = (id, event) => {
    const { name, value } = event.target;
    setData(data.map(project => project.id === id ? { ...project, [name]: value } : project));
  };

  const saveEdit = (id) => {
    const projectToUpdate = data.find(project => project.id === id);
    updateProject(id, {
      title: projectToUpdate.title,
      contents: projectToUpdate.contents,
      stack: projectToUpdate.stack,
      team_name: projectToUpdate.team_name,
      team_members: projectToUpdate.team_members,
    });
    toggleEdit(id);
  };

  return (
    <div className="container">
      {data.map((d, i) => (
        <div key={i} className="card">
          {d.isEditing ? (
            <>
              <input
                type="text"
                name="title"
                value={d.title}
                onChange={(e) => handleEditChange(d.id, e)}
                className="edit-input"
              />
              <textarea
                name="contents"
                value={d.contents}
                onChange={(e) => handleEditChange(d.id, e)}
                className="edit-input"
              ></textarea>
              {/* Additional inputs for stack, team_name, team_members */}
            </>
          ) : (
            <>
              <p><strong>Title:</strong> {d.title}</p>
              <p><strong>Description:</strong> {d.contents}</p>
              {/* Static display of other fields */}
            </>
          )}
          <button className="button-delete" onClick={() => deleteProject(d.id)}>Delete</button>
          <button className="button-edit" onClick={() => toggleEdit(d.id)}>
            {d.isEditing ? 'Cancel' : 'Edit'}
          </button>
          {d.isEditing && (
            <button className="button-save" onClick={() => saveEdit(d.id)}>Save</button>
          )}
        </div>
      ))}
      <AddProjectForm onAdd={addProject} />
    </div>
  );
}

export default App
