import React, { useState, useEffect } from 'react';
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message, onHide }) {
    const [isHovering, setIsHovering] = useState(false);

    useEffect(() => {
        let timeout;
        if (show && !isHovering) {
            timeout = setTimeout(() => {
                onHide(); // Assuming onHide is a prop method to hide the toast
            }, 3000); // Hide toast after 3 seconds if not hovered
        }

        return () => clearTimeout(timeout); // Cleanup timeout
    }, [show, isHovering, onHide]);

    if (!show) {
        return null;
    }

    return (
        <div
            className="toast"
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
        >
            <Check className="toast-icon" size={24} color="#4CAF50" />
            <span className="toast-message">{message}</span>
            <button className="toast-view-button">View</button>
        </div>
    );
}

export default Toast;
