import React, { useEffect } from 'react';
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message, onHide }) { // Assuming onHide is a function to handle hiding the toast
  useEffect(() => {
    if (show) {
      // Set timeout for 30 seconds + 1 second for fade-out animation
      const timer = setTimeout(() => {
        onHide(); // Call the onHide function to change the state outside of this component
      }, 31000); // Adjust this duration as needed

      return () => clearTimeout(timer);
    }
  }, [show, onHide]);

  if (!show) {
    return null;
  }

  return (
    <div className="toast">
      <Check className="toast-icon" size={24} color="#4CAF50" />
      <span className="toast-message">{message}</span>
      <button className="toast-view-button">View</button>
    </div>
  );
}

export default Toast;
