import React, { useEffect, useState } from 'react'
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message }) {
    const [isHovering, setIsHovering] = useState(false);

    // useEffect to handle the toast visibility with hovering
    useEffect(() => {
        let timeout;
        if (show && !isHovering) {
            timeout = setTimeout(() => {
                // Your method to hide the toast
                // For example, you might call a function passed via props that updates state in the parent component to hide the toast
            }, 3000); // Adjust time as needed
        }

        return () => clearTimeout(timeout); // Cleanup on unmount or when dependencies change
    }, [show, isHovering]); // Add any other dependencies you might need, like a method to hide the toast

    if (!show) {
        return null;
    }

    return (
        <div
            className="toast"
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
        >
            <Check className="toast-icon" size={24} color="#4CAF50" />
            <span className="toast-message">{message}</span>
            <button className="toast-view-button">View</button>
        </div>
    );
}

  export default Toast;
