import React, { useState, useEffect } from 'react';
import './EditProjectModal.css'; // Assuming you store modal styles in a separate CSS file

function EditProjectModal({ project, isOpen, onSave, onCancel }) {
  const [editedProject, setEditedProject] = useState({
    title: '',
    contents: '',
    stack: '',
    team_name: '',
    team_members: '',
    // Initialize other fields as needed
  });

  useEffect(() => {
    if (project) {
      setEditedProject({
        title: project.title || '',
        contents: project.contents || '',
        stack: project.stack || '',
        team_name: project.team_name || '',
        team_members: project.team_members || '',
        // Copy other fields as needed
      });
    }
  }, [project, isOpen]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProject((prev) => ({ ...prev, [name]: value }));
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-card">
        <input
          type="text"
          name="title"
          value={editedProject.title}
          onChange={handleChange}
          placeholder="Title"
        />
        <textarea
          name="contents"
          value={editedProject.contents}
          onChange={handleChange}
          placeholder="Description"
        />
        <input
          type="text"
          name="stack"
          value={editedProject.stack}
          onChange={handleChange}
          placeholder="Technology Stack"
        />
        {/* Repeat for other fields */}
        <div className="modal-actions">
          <button onClick={() => onSave(editedProject)}>Save</button>
          <button onClick={onCancel}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default EditProjectModal;
