import React, { useState } from 'react';
import './SideNavigation.css';

const SideNavigation = ({ onSort }) => {
  // Initialize state to track the selected sort option
  const [selectedSort, setSelectedSort] = useState(null);

  // Update both the selected sort state and perform the sorting when a button is clicked
  const handleSort = (sortKey, sortOrder = '') => {
    setSelectedSort(sortKey + sortOrder); // Update the selected sort option
    onSort(sortKey, sortOrder);
  };

  return (
    <div className="side-navigation">
      <h3>Sort By</h3>
      <button
        className={selectedSort === 'datedesc' ? 'selected' : ''}
        onClick={() => handleSort('date', 'desc')}
      >
        Date (Newest)
      </button>
      <button
        className={selectedSort === 'dateasc' ? 'selected' : ''}
        onClick={() => handleSort('date', 'asc')}
      >
        Date (Oldest)
      </button>
      <button
        className={selectedSort === 'status' ? 'selected' : ''}
        onClick={() => handleSort('status')}
      >
        Status
      </button>
    </div>
  );
};

export default SideNavigation;
