import React, { useEffect, useState } from 'react';
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message }) {
  const [shouldShow, setShouldShow] = useState(show);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    if (show) {
      setShouldShow(true);
      setFadeOut(false);
      const timer = setTimeout(() => {
        setFadeOut(true); // Start fade out
        setTimeout(() => setShouldShow(false), 500); // Wait for fade out to finish, then hide
      }, 30000); // Adjust display time here

      return () => clearTimeout(timer);
    }
  }, [show]);

  if (!shouldShow) {
    return null;
  }

  return (
    <div className={`toast ${fadeOut ? 'fade-out' : ''}`}>
      <Check className="toast-icon" size={24} color="#4CAF50" /> {/* Checkmark icon */}
      <span className="toast-message">{message}</span>
      <button className="toast-view-button">View</button> {/* View as a button */}
    </div>
  );
}

export default Toast;
