import React, { useEffect, useState } from 'react';
import './Toast.css';
import { Check } from 'lucide-react';

function Toast({ show, message }) {
  const [isVisible, setIsVisible] = useState(false);
  const [animationClass, setAnimationClass] = useState('fade-in');

  useEffect(() => {
    if (show) {
      setIsVisible(true);
      setAnimationClass('fade-in');

      // Start fade out after 30 seconds
      const timer = setTimeout(() => {
        setAnimationClass('fade-out');
        // Remove the toast after fade-out animation completes
        setTimeout(() => setIsVisible(false), 500); // Fade-out duration
      }, 30000); // Time before fade-out starts

      return () => clearTimeout(timer);
    }
  }, [show]);

  if (!isVisible) {
    return null;
  }

  return (
    <div className={`toast ${animationClass}`}>
      <Check className="toast-icon" size={24} color="#4CAF50" />
      <span className="toast-message">{message}</span>
      <button className="toast-view-button">View</button>
    </div>
  );
}

export default Toast;
